<?php
/**
 * Simple PHP script to handle form submissions without a third-party library.
 * This script sends an HTML-formatted email.
 */

// Replace this with your actual receiving email address
$receiving_email_address = 'info@morendibuilders.com';

if ($_SERVER["REQUEST_METHOD"] == "POST") {

    // Sanitize and validate inputs
    $name = htmlspecialchars(trim($_POST['name'] ?? ''));
    $email = filter_var(trim($_POST['email'] ?? ''), FILTER_VALIDATE_EMAIL);
    $subject = htmlspecialchars(trim($_POST['subject'] ?? ''));
    $message = htmlspecialchars(trim($_POST['message'] ?? ''));

    // Check if required fields are filled
    if (empty($name) || empty($email) || empty($message)) {
        die('Please fill in all required fields.');
    }

    // Construct the email subject
    $email_subject = "From website: " . $subject;
    
    // Construct the HTML email body with inline CSS
    $email_body = '
    <!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">
    <html xmlns="http://www.w3.org/1999/xhtml">
    <head>
      <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />
      <title>New Message</title>
      <meta name="viewport" content="width=device-width, initial-scale=1.0"/>
    </head>
    <body style="margin: 0; padding: 0;">
      <table border="0" cellpadding="0" cellspacing="0" width="100%">
        <tr>
          <td style="padding: 20px 0 30px 0;">
            <table align="center" border="0" cellpadding="0" cellspacing="0" width="600" style="border-collapse: collapse;">
              <!-- Header -->
              <tr>
                <td align="center" style="padding: 40px 0; background-color: #373b3e; color: #ffffff;">
                  <table border="0" cellpadding="0" cellspacing="0" width="100%">
                    <tr>
                      <td align="center">
                        <img src="data:image/png;base64,"" alt="Company Logo" style="width: 50px; height: auto; display: block; margin-bottom: 10px;" />
                      </td>
                    </tr>
                    <tr>
                      <td align="center" style="font-family: Arial, sans-serif; font-size: 24px; padding-top: 10px;">
                        Client Inquiry
                      </td>
                    </tr>
                  </table>
                </td>
              </tr>
              <!-- Body -->
              <tr>
                <td bgcolor="#ffffff" style="padding: 40px 30px 40px 30px; border: 1px solid #e1e1e1;">
                  <table border="0" cellpadding="0" cellspacing="0" width="100%">
                    
                    <tr>
                      <td style="font-family: Arial, sans-serif; font-size: 16px; color: #333333; padding: 20px 0 10px 0;">
                        Kindly reply to me and let me know how you can help with my inquiry:
                      </td>
                    </tr>
                    <tr>
                      <td style="font-family: Arial, sans-serif; font-size: 16px; color: #333333;">
                        <strong>Name:</strong> ' . $name . '
                      </td>
                    </tr>
                    <tr>
                      <td style="font-family: Arial, sans-serif; font-size: 16px; color: #333333;">
                        <strong>Email:</strong> ' . $email . '
                      </td>
                    </tr>
                    <tr>
                      <td style="font-family: Arial, sans-serif; font-size: 16px; color: #333333;">
                        <strong>Subject:</strong> ' . $subject . '
                      </td>
                    </tr>
                    <tr>
                      <td style="font-family: Arial, sans-serif; font-size: 16px; color: #333333;">
                        <strong>Message:</strong><br />
                        ' . nl2br($message) . '
                      </td>
                    </tr>
                    <tr>
                      <td align="center" style="padding: 30px 0 0 0;">
                        <a href="mailto:' . $email . '" style="display: inline-block; padding: 10px 25px; font-family: Arial, sans-serif; font-size: 16px; color: #ffffff; background-color: #dc3545; text-decoration: none; border-radius: 5px;">Reply</a>
                      </td>
                    </tr>
                    <tr>
                      <td style="font-family: Arial, sans-serif; font-size: 16px; color: #333333; padding: 20px 0;">
                        Thanks,<br/>
                        The Morendi Builders and Contractors Team
                      </td>
                    </tr>
                  </table>
                </td>
              </tr>
              <!-- Footer -->
              <tr>
                <td align="center" style="padding: 20px 0; font-family: Arial, sans-serif; font-size: 12px; color: #999999; background-color: #f4f4f4;">
                  Â© 2025 Morendi builders and contractors ltd. All rights reserved.
                </td>
              </tr>
            </table>
          </td>
        </tr>
      </table>
    </body>
    </html>';

    // Set the headers for an HTML email
    $headers = "From: noreply@morendibuilders.com\r\n";
    $headers .= "Reply-To: $email\r\n";
    $headers .= "MIME-Version: 1.0\r\n";
    $headers .= "Content-Type: text/html; charset=UTF-8\r\n";

    // Send the email
    if (mail($receiving_email_address, $email_subject, $email_body, $headers)) {
        die('OK'); // The Javascript expects this specific string for success
    } else {
        die('An error occurred while trying to send your message.');
    }

} else {
    die('Invalid request method.');
}
?>
